function select(e){return document.querySelector(e)}function selectAll(e){return document.querySelectorAll(e)}function print(e){return console.log(e)}function getInputValue(e){return document.querySelector(e).value}function onClick(e,n){return document.querySelector(e).addEventListener("click",n)}function onChange(e,n){return document.querySelector(e).onchange=n}function changeHTML(e,n){return document.querySelector(e).innerHTML=n}